import React, { useState } from "react";
import { sendEmail, notify } from "../utils/fetch";
import "./style.css";

export default function Contact() {
  const [message, setMessage] = useState("");
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");

  const handleChange = (e) => {
    let changeName = e.target.name;
    let currValue = e.target.value;
    if (changeName === "message") {
      setMessage(currValue);
    } else if (changeName === "name") {
      setName(currValue);
    } else {
      setEmail(currValue);
    }
  };

  function triggerSendEmails(event) {
    if (!message || !name || !email) {
      alert("Please fill in all the information so I can reply to you");
    } else {
      const sendRegisterRequest = async () => {
        let data = new URLSearchParams();
        data.append("message", message);
        data.append("name", name);
        data.append("email", email);
        setEmail("");
        setMessage("");
        setName("");
        alert("Your message has been sent! üìÆ");
        await sendEmail(data);
        await notify(data);
      };

      sendRegisterRequest();
    }
  }

  return (
    <div className="contact-container">
      <h2 className="contact-heading">
        ‚úâÔ∏è Contact Me
      </h2>

      <p className="contact-subheading">
        Feel free to leave me a message below :)
      </p>

      <div className="contact-field">
        <div className="contact-label">
          <label htmlFor="message">Your Message</label>
        </div>
        <div className="contact-input">
          <textarea
            id="message"
            name="message"
            value={message}
            onChange={handleChange}
          />
        </div>
      </div>

      <div className="contact-field">
        <div className="contact-label">
          <label htmlFor="name">Your Name</label>
        </div>
        <div className="contact-input">
          <input
            type="text"
            id="name"
            name="name"
            value={name}
            onChange={handleChange}
          />
        </div>
      </div>

      <div className="contact-field">
        <div className="contact-label">
          <label htmlFor="email">Your Email</label>
        </div>
        <div className="contact-input">
          <input
            type="text"
            id="email"
            name="email"
            value={email}
            onChange={handleChange}
          />
        </div>
      </div>

      <button
        className="contact-button"
        onClick={triggerSendEmails}
      >
        Send Message
      </button>
    </div>
  );
}
